import React, { useState } from 'react';

import './Expenses.css'
import ExpenseItem from "./ExpenseItem";
import ExpensesFilter from '../ExpensesFilter/ExpensesFilter';
import Card from '../UI/Card'

function Expenses({ expenses }) {

    const [filterYear, setFilterYear] = useState('all')

    // const filteredExpenses = (filterYear) => {
    //     if (filterYear === 'all') {
    //         expenses.map((expense) => {
    //             return (
    //                 <ExpenseItem
    //                     key={expense.id}
    //                     title={expense.title}
    //                     amount={expense.amount}
    //                     date={expense.date}
    //                 />
    //             )
    //         })
    //     }
    // }

    const filteredExpenses = () => {
        expenses.filter((expense) => {
            return expense.date.getFullYear().toString() === filterYear
        })
    } 

    const renderExpenseItems = expenses.map((expense) => {
        const expenseItem = (
            <ExpenseItem
                key={expense.id}
                title={expense.title}
                amount={expense.amount}
                date={expense.date}
            />
        )
        if (filterYear === 'all') return expenseItem
        else if (expense.date.getFullYear().toString() === filterYear) return expenseItem
    })
    console.log(renderExpenseItems.length)

    return (
        <div className='expenses'>
            <ExpensesFilter 
                filterYear={filterYear}
                setFilterYear={setFilterYear} 
            />
            <Card className="expenses">
                {renderExpenseItems}
            </Card>
        </div>
    )
}

export default Expenses
