import React, { useState } from 'react';

import './Expenses.css'
import ExpenseItem from "./ExpenseItem";
import ExpensesFilter from '../ExpensesFilter/ExpensesFilter';
import ExpensesList from './ExpensesList';
import Card from '../UI/Card'

function Expenses({ expenses }) {

    const [filterYear, setFilterYear] = useState('all')

    let filteredExpenses

    if (filterYear === 'all') filteredExpenses = expenses

    else filteredExpenses = expenses.filter((expense) => expense.date.getFullYear().toString() === filterYear)

    const renderExpenseItems = filteredExpenses.map((expense) => {
        const expenseItem = (
            <ExpenseItem
                key={expense.id}
                title={expense.title}
                amount={expense.amount}
                date={expense.date}
            />
        )
        if (filterYear === 'all') return expenseItem
        else if (expense.date.getFullYear().toString() === filterYear) return expenseItem
    })

    const noContent = <p>There is no content to display.</p>

    return (
        <div className='expenses'>
            <Card className="expenses">
                <ExpensesFilter 
                    filterYear={filterYear}
                    setFilterYear={setFilterYear} 
                />
                <ExpensesList expenses={filteredExpenses} />
                {filteredExpenses.length ? renderExpenseItems : noContent}
            </Card>
        </div>
    )
}

export default Expenses
